	//HOME TEASER PAGER

	function pagerUpdate(){

		$('.pager li ').each(function(i){

			var current = home_teaser.getCurrentSlide();
			if( i  == current){
				$(this).addClass('active');
			}
			else{
				$(this).removeClass('active');
			}

		});

	}

	$('.pager a').each(function(i){

		$(this).click(function(e){

			e.preventDefault();
			home_teaser.goToSlide(i);

		});

	});

